from typing import List, Optional

__version__ = "24.0"


def main(args: Optional[List[str]] = None) -> int:
    """
    Internal entry point used by pip's console scripts.
    
    Delegates execution to pip._internal.utils.entrypoints._wrapper and returns its integer exit code.
    
    Parameters:
        args (Optional[List[str]]): Optional list of command-line-style arguments to pass to pip.
            If None, the underlying wrapper uses its default argument resolution.
    
    Returns:
        int: Process exit code produced by the underlying entrypoint wrapper.
    """
    from pip._internal.utils.entrypoints import _wrapper

    return _wrapper(args)
