from typing import List, Optional


def main(args: Optional[List[str]] = None) -> int:
    """
    Backward-compatible console entry point that delegates to pip._internal.utils.entrypoints._wrapper.
    
    Parameters:
        args (Optional[List[str]]): Command-line arguments to pass to the underlying entrypoint wrapper; if None, the wrapper will use sys.argv.
    
    Returns:
        int: Process exit code returned by the underlying wrapper.
    """
    from pip._internal.utils.entrypoints import _wrapper

    return _wrapper(args)
