import axios, { AxiosInstance, AxiosResponse } from 'axios'
import { ApiResponse, User, CodeRequest, ChatSession, SupportTicket, Subscription } from '@/types'
import { API_CONFIG } from './config'

class ApiClient {
  private client: AxiosInstance
  private baseURL: string

  constructor() {
    this.baseURL = API_CONFIG.baseUrl

    this.client = axios.create({
      baseURL: this.baseURL,
      timeout: API_CONFIG.timeoutMs,
      headers: {
        'Content-Type': 'application/json',
      },
    })

    // Request interceptor to add auth token
    this.client.interceptors.request.use(
      (config) => {
        const token = this.getAuthToken()
        if (token) {
          config.headers.Authorization = `Bearer ${token}`
        }
        return config
      },
      (error) => Promise.reject(error)
    )

    // Response interceptor for error handling
    this.client.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401) {
          this.handleUnauthorized()
        }
        return Promise.reject(error)
      }
    )
  }

  private getAuthToken(): string | null {
    if (typeof window !== 'undefined') {
      return localStorage.getItem('auth_token')
    }
    return null
  }

  private handleUnauthorized(): void {
    if (typeof window !== 'undefined') {
      localStorage.removeItem('auth_token')
      window.location.href = '/login'
    }
  }

  // Auth endpoints
  async login(email: string, password: string): Promise<ApiResponse<{ user: User; token: string }>> {
    const response = await this.client.post('/api/auth/login', { email, password })
    return response.data
  }

  async register(data: {
    name: string
    email: string
    password: string
    tenantName?: string
  }): Promise<ApiResponse<{ user: User; token: string }>> {
    const response = await this.client.post('/api/auth/register', data)
    return response.data
  }

  async logout(): Promise<ApiResponse> {
    const response = await this.client.post('/api/auth/logout')
    return response.data
  }

  async getCurrentUser(): Promise<ApiResponse<User>> {
    const response = await this.client.get('/api/auth/me')
    return response.data
  }

  // Model Router endpoints - Updated to match backend API
  async routeRequest(prompt: string, options?: {
    model?: string
    temperature?: number
    maxTokens?: number
    taskType?: string
    priority?: string
    systemPrompt?: string
    context?: any
  }): Promise<ApiResponse<any>> {
    const requestId = Date.now().toString()
    const response = await this.client.post('/model/route', {
      text: prompt,
      request_id: requestId,
      temperature: options?.temperature || 0.7,
      max_tokens: options?.maxTokens || 4096,
      task_type: options?.taskType,
      priority: options?.priority || 'medium',
      system_prompt: options?.systemPrompt,
      context: options?.context,
    })
    return response.data
  }

  async getAvailableModels(): Promise<ApiResponse<any>> {
    const response = await this.client.get('/model/models')
    return response.data
  }

  async getModelStats(): Promise<ApiResponse<any>> {
    const response = await this.client.get('/model/stats')
    return response.data
  }

  async testClaudeConnection(): Promise<ApiResponse<any>> {
    const response = await this.client.post('/model/health/claude')
    return response.data
  }

  // Plan Management endpoints
  async createPlan(data: {
    title: string
    description: string
    requirements: string[]
  }): Promise<ApiResponse<any>> {
    const response = await this.client.post('/api/plans/create', data)
    return response.data
  }

  async getPlans(): Promise<ApiResponse<any[]>> {
    const response = await this.client.get('/api/plans')
    return response.data
  }

  async getPlan(id: string): Promise<ApiResponse<any>> {
    const response = await this.client.get(`/api/plans/${id}`)
    return response.data
  }

  // Git Worktree endpoints
  async createWorktree(data: {
    repository: string
    branch: string
    name: string
  }): Promise<ApiResponse<any>> {
    const response = await this.client.post('/api/git/create', data)
    return response.data
  }

  async getWorktrees(): Promise<ApiResponse<any[]>> {
    const response = await this.client.get('/api/git/list')
    return response.data
  }

  // Workflow Orchestrator endpoints
  async createWorkflow(data: {
    name: string
    description: string
    steps: any[]
  }): Promise<ApiResponse<any>> {
    const response = await this.client.post('/api/workflows/create', data)
    return response.data
  }

  async executeWorkflow(id: string): Promise<ApiResponse<any>> {
    const response = await this.client.post(`/api/workflows/${id}/execute`)
    return response.data
  }

  async getWorkflows(): Promise<ApiResponse<any[]>> {
    const response = await this.client.get('/api/workflows')
    return response.data
  }

  // Verification Feedback endpoints
  async submitFeedback(data: {
    type: string
    title: string
    description: string
    metadata?: any
  }): Promise<ApiResponse<any>> {
    const response = await this.client.post('/api/feedback/submit', data)
    return response.data
  }

  async getFeedback(): Promise<ApiResponse<any[]>> {
    const response = await this.client.get('/api/feedback/list')
    return response.data
  }

  // Chat endpoints
  async createChatSession(title?: string): Promise<ApiResponse<ChatSession>> {
    const response = await this.client.post('/api/chat/sessions', { title })
    return response.data
  }

  async getChatSessions(): Promise<ApiResponse<ChatSession[]>> {
    const response = await this.client.get('/api/chat/sessions')
    return response.data
  }

  async sendChatMessage(sessionId: string, message: string): Promise<ApiResponse<any>> {
    const response = await this.client.post(`/api/chat/sessions/${sessionId}/messages`, {
      content: message,
    })
    return response.data
  }

  // Support endpoints
  async createTicket(data: {
    title: string
    description: string
    priority: 'low' | 'medium' | 'high' | 'urgent'
  }): Promise<ApiResponse<SupportTicket>> {
    const response = await this.client.post('/api/support/tickets', data)
    return response.data
  }

  async getTickets(): Promise<ApiResponse<SupportTicket[]>> {
    const response = await this.client.get('/api/support/tickets')
    return response.data
  }

  async updateTicket(id: string, data: Partial<SupportTicket>): Promise<ApiResponse<SupportTicket>> {
    const response = await this.client.patch(`/api/support/tickets/${id}`, data)
    return response.data
  }

  // Billing endpoints
  async getSubscription(): Promise<ApiResponse<Subscription>> {
    const response = await this.client.get('/api/billing/subscription')
    return response.data
  }

  async updateSubscription(planId: string): Promise<ApiResponse<Subscription>> {
    const response = await this.client.post('/api/billing/subscription/update', { planId })
    return response.data
  }

  async getInvoices(): Promise<ApiResponse<any[]>> {
    const response = await this.client.get('/api/billing/invoices')
    return response.data
  }

  async downloadInvoice(invoiceId: string): Promise<Blob> {
    const response = await this.client.get(`/api/billing/invoices/${invoiceId}/pdf`, {
      responseType: 'blob',
    })
    return response.data
  }

  // Video Library endpoints
  async getVideos(): Promise<ApiResponse<any[]>> {
    const response = await this.client.get('/api/videos')
    return response.data
  }

  async getVideo(id: string): Promise<ApiResponse<any>> {
    const response = await this.client.get(`/api/videos/${id}`)
    return response.data
  }

  // Analytics endpoints
  async getAnalytics(timeRange: string): Promise<ApiResponse<any>> {
    const response = await this.client.get(`/api/analytics?range=${timeRange}`)
    return response.data
  }

  async getDashboardStats(): Promise<ApiResponse<any>> {
    const response = await this.client.get('/api/analytics/dashboard')
    return response.data
  }

  // Health check
  async healthCheck(): Promise<ApiResponse<any>> {
    const response = await this.client.get('/health')
    return response.data
  }

  // System status
  async getSystemStatus(): Promise<ApiResponse<any>> {
    const response = await this.client.get('/status')
    return response.data
  }
}

// Create singleton instance
export const apiClient = new ApiClient()

// Export individual service objects for better organization
export const authApi = {
  login: apiClient.login.bind(apiClient),
  register: apiClient.register.bind(apiClient),
  logout: apiClient.logout.bind(apiClient),
  getCurrentUser: apiClient.getCurrentUser.bind(apiClient),
}

export const modelApi = {
  routeRequest: apiClient.routeRequest.bind(apiClient),
  getAvailableModels: apiClient.getAvailableModels.bind(apiClient),
  getModelStats: apiClient.getModelStats.bind(apiClient),
  testClaudeConnection: apiClient.testClaudeConnection.bind(apiClient),
}

export const planApi = {
  createPlan: apiClient.createPlan.bind(apiClient),
  getPlans: apiClient.getPlans.bind(apiClient),
  getPlan: apiClient.getPlan.bind(apiClient),
}

export const gitApi = {
  createWorktree: apiClient.createWorktree.bind(apiClient),
  getWorktrees: apiClient.getWorktrees.bind(apiClient),
}

export const workflowApi = {
  createWorkflow: apiClient.createWorkflow.bind(apiClient),
  executeWorkflow: apiClient.executeWorkflow.bind(apiClient),
  getWorkflows: apiClient.getWorkflows.bind(apiClient),
}

export const feedbackApi = {
  submitFeedback: apiClient.submitFeedback.bind(apiClient),
  getFeedback: apiClient.getFeedback.bind(apiClient),
}

export const chatApi = {
  createChatSession: apiClient.createChatSession.bind(apiClient),
  getChatSessions: apiClient.getChatSessions.bind(apiClient),
  sendChatMessage: apiClient.sendChatMessage.bind(apiClient),
}

export const supportApi = {
  createTicket: apiClient.createTicket.bind(apiClient),
  getTickets: apiClient.getTickets.bind(apiClient),
  updateTicket: apiClient.updateTicket.bind(apiClient),
}

export const billingApi = {
  getSubscription: apiClient.getSubscription.bind(apiClient),
  updateSubscription: apiClient.updateSubscription.bind(apiClient),
  getInvoices: apiClient.getInvoices.bind(apiClient),
  downloadInvoice: apiClient.downloadInvoice.bind(apiClient),
}

// Export the axios instance for direct use
export const api = apiClient['client']

export default apiClient

/**
 * Staff API client for backend integration
 */

import { api } from './api'

// Base staff API URL
const STAFF_API_BASE = '/api/staff'

// Types
export interface User {
  id: string
  name: string
  email: string
  role: 'customer' | 'admin' | 'manager' | 'support' | 'content'
  status: 'active' | 'inactive' | 'suspended'
  plan: string
  last_active?: Date
  created_at: Date
  updated_at: Date
  total_spent: number
  api_calls: number
}

export interface UserCreate {
  name: string
  email: string
  role: 'customer' | 'admin' | 'manager' | 'support' | 'content'
  status?: 'active' | 'inactive' | 'suspended'
  password: string
}

export interface UserUpdate {
  name?: string
  email?: string
  role?: 'customer' | 'admin' | 'manager' | 'support' | 'content'
  status?: 'active' | 'inactive' | 'suspended'
}

export interface Ticket {
  id: string
  title: string
  description: string
  status: 'open' | 'in-progress' | 'waiting-customer' | 'resolved' | 'closed'
  priority: 'low' | 'medium' | 'high' | 'urgent'
  category: string
  customer: {
    name: string
    email: string
    plan: string
  }
  assigned_to?: string
  created_at: Date
  updated_at: Date
  response_time?: number
  message_count: number
}

export interface TicketCreate {
  title: string
  description: string
  priority: 'low' | 'medium' | 'high' | 'urgent'
  category: string
  customer_id: string
}

export interface TicketUpdate {
  title?: string
  description?: string
  status?: 'open' | 'in-progress' | 'waiting-customer' | 'resolved' | 'closed'
  priority?: 'low' | 'medium' | 'high' | 'urgent'
  category?: string
  assigned_to?: string
}

export interface SystemStats {
  total_users: number
  active_users: number
  total_subscriptions: number
  active_subscriptions: number
  open_tickets: number
  closed_tickets: number
  system_uptime: string
  avg_response_time: number
}

export interface DashboardData {
  system_stats: SystemStats
  recent_tickets: Ticket[]
  system_alerts: Array<{
    id: number
    type: string
    message: string
    time: string
  }>
  quick_actions: Array<{
    name: string
    description: string
    href: string
    icon: string
    color: string
  }>
}

export interface ListResponse<T> {
  items: T[]
  total: number
  page: number
  per_page: number
}

export interface StaffResponse {
  success: boolean
  message: string
  data?: any
  timestamp: string
}

// Staff API Service
export class StaffApiService {
  // Dashboard & Stats
  static async getDashboard(): Promise<DashboardData> {
    const response = await api.get(`${STAFF_API_BASE}/dashboard`)
    return response.data
  }

  static async getSystemStats(): Promise<SystemStats> {
    const response = await api.get(`${STAFF_API_BASE}/stats`)
    return response.data
  }

  static async getSystemHealth(): Promise<any> {
    const response = await api.get(`${STAFF_API_BASE}/health/system`)
    return response.data
  }

  // User Management
  static async getUsers(params?: {
    page?: number
    per_page?: number
    role?: string
    status?: string
    search?: string
  }): Promise<ListResponse<User>> {
    const response = await api.get(`${STAFF_API_BASE}/users`, { params })
    return {
      items: response.data.users,
      total: response.data.total,
      page: response.data.page,
      per_page: response.data.per_page
    }
  }

  static async getUser(id: string): Promise<User> {
    const response = await api.get(`${STAFF_API_BASE}/users/${id}`)
    return response.data
  }

  static async createUser(userData: UserCreate): Promise<StaffResponse> {
    const response = await api.post(`${STAFF_API_BASE}/users`, userData)
    return response.data
  }

  static async updateUser(id: string, userData: UserUpdate): Promise<StaffResponse> {
    const response = await api.put(`${STAFF_API_BASE}/users/${id}`, userData)
    return response.data
  }

  static async deleteUser(id: string): Promise<StaffResponse> {
    const response = await api.delete(`${STAFF_API_BASE}/users/${id}`)
    return response.data
  }

  static async suspendUser(id: string): Promise<StaffResponse> {
    const response = await api.patch(`${STAFF_API_BASE}/users/${id}/suspend`)
    return response.data
  }

  static async activateUser(id: string): Promise<StaffResponse> {
    const response = await api.patch(`${STAFF_API_BASE}/users/${id}/activate`)
    return response.data
  }

  // Ticket Management
  static async getTickets(params?: {
    page?: number
    per_page?: number
    status?: string
    priority?: string
    category?: string
    search?: string
  }): Promise<ListResponse<Ticket>> {
    const response = await api.get(`${STAFF_API_BASE}/tickets`, { params })
    return {
      items: response.data.tickets,
      total: response.data.total,
      page: response.data.page,
      per_page: response.data.per_page
    }
  }

  static async getTicket(id: string): Promise<Ticket> {
    const response = await api.get(`${STAFF_API_BASE}/tickets/${id}`)
    return response.data
  }

  static async createTicket(ticketData: TicketCreate): Promise<StaffResponse> {
    const response = await api.post(`${STAFF_API_BASE}/tickets`, ticketData)
    return response.data
  }

  static async updateTicket(id: string, ticketData: TicketUpdate): Promise<StaffResponse> {
    const response = await api.put(`${STAFF_API_BASE}/tickets/${id}`, ticketData)
    return response.data
  }

  static async assignTicket(id: string, assignTo: string): Promise<StaffResponse> {
    const response = await api.patch(`${STAFF_API_BASE}/tickets/${id}/assign?assign_to=${assignTo}`)
    return response.data
  }

  static async updateTicketStatus(id: string, status: string): Promise<StaffResponse> {
    const response = await api.patch(`${STAFF_API_BASE}/tickets/${id}/status?new_status=${status}`)
    return response.data
  }

  static async updateTicketPriority(id: string, priority: string): Promise<StaffResponse> {
    const response = await api.patch(`${STAFF_API_BASE}/tickets/${id}/priority?new_priority=${priority}`)
    return response.data
  }

  static async getTicketStats(): Promise<any> {
    const response = await api.get(`${STAFF_API_BASE}/tickets/stats`)
    return response.data
  }
}

// React hooks for staff API
export function useStaffApi() {
  return StaffApiService
}

// Error handling helper
export function handleStaffApiError(error: any): string {
  if (error.response?.data?.message) {
    return error.response.data.message
  }
  if (error.response?.status === 401) {
    return 'Unauthorized access. Please check your permissions.'
  }
  if (error.response?.status === 403) {
    return 'Forbidden. You do not have permission to perform this action.'
  }
  if (error.response?.status === 404) {
    return 'Resource not found.'
  }
  if (error.response?.status >= 500) {
    return 'Server error. Please try again later.'
  }
  return error.message || 'An unexpected error occurred.'
}

