// User and Authentication Types
export interface User {
  id: string
  email: string
  name: string
  avatar?: string
  role: UserRole
  plan: UserPlan
  tenantId: string
  createdAt: Date
  lastLoginAt?: Date
  preferences: UserPreferences
  status: 'active' | 'inactive' | 'suspended'
}

export interface UserPreferences {
  theme: 'dark' | 'light'
  language: string
  notifications: {
    email: boolean
    push: boolean
    billing: boolean
    updates: boolean
  }
  editor: {
    fontSize: number
    theme: string
    autoSave: boolean
    wordWrap: boolean
  }
}

export type UserRole = 
  | 'superadmin' 
  | 'admin' 
  | 'marketing' 
  | 'sales' 
  | 'billing' 
  | 'support' 
  | 'tech' 
  | 'finance' 
  | 'legal' 
  | 'customer'

export type UserPlan = 'free' | 'standard' | 'pro' | 'enterprise'

// Tenant and Organization Types
export interface Tenant {
  id: string
  name: string
  domain: string
  plan: UserPlan
  status: 'active' | 'suspended' | 'trial'
  settings: TenantSettings
  usage: TenantUsage
  createdAt: Date
  expiresAt?: Date
}

export interface TenantSettings {
  branding: {
    logo?: string
    primaryColor: string
    secondaryColor: string
  }
  features: {
    codeGeneration: boolean
    collaboration: boolean
    videoLibrary: boolean
    advancedAnalytics: boolean
  }
  limits: {
    users: number
    storage: number
    apiCalls: number
  }
}

export interface TenantUsage {
  users: number
  storage: number
  apiCalls: number
  lastResetAt: Date
}

// Code Generation and AI Types
export interface CodeRequest {
  id: string
  prompt: string
  language: string
  model: string
  userId: string
  tenantId: string
  status: 'pending' | 'processing' | 'completed' | 'failed'
  result?: CodeResult
  createdAt: Date
  completedAt?: Date
}

export interface CodeResult {
  code: string
  explanation: string
  metadata: {
    tokensUsed: number
    processingTime: number
    confidence: number
  }
}

// Chat and RAG Types
export interface ChatSession {
  id: string
  userId: string
  tenantId: string
  title: string
  messages: ChatMessage[]
  context: ChatContext
  createdAt: Date
  updatedAt: Date
}

export interface ChatMessage {
  id: string
  role: 'user' | 'assistant' | 'system'
  content: string
  metadata?: {
    sources?: string[]
    confidence?: number
    tokensUsed?: number
  }
  timestamp: Date
}

export interface ChatContext {
  documentIds: string[]
  tags: string[]
  priority: number
}

// Content Management Types
export interface BlogPost {
  id: string
  title: string
  slug: string
  content: string
  excerpt: string
  authorId: string
  status: 'draft' | 'published' | 'archived'
  tags: string[]
  featuredImage?: string
  publishedAt?: Date
  createdAt: Date
