  { name: 'User Management', href: '/staff/users', icon: UserGroupIcon, roles: ['admin', 'manager'] },
  { name: 'Support Tickets', href: '/staff/tickets', icon: LifebuoyIcon, roles: ['admin', 'manager', 'support'] },
