              <Link href="/cookies" className="text-gray-500 hover:text-white transition-colors">Cookies</Link>
            </div>
          </div>
        </div>
      </footer>

      {/* Product Tour */}
      <ProductTour 
        isOpen={showTour} 
        onClose={() => setShowTour(false)} 
      />

