      const timer = setTimeout(() => {
        if (currentStep < demoSteps.length - 1) {
          setCurrentStep(currentStep + 1)
        } else {
          setIsPlaying(false)
        }
      }, isPlaying ? 3000 : 10000)
      return () => clearTimeout(timer)
    }
  }, [isOpen, currentStep, isPlaying, demoSteps.length])

  useEffect(() => {
    if (isOpen) {
      setCode(demoSteps[0].code)
      setCurrentStep(0)
      setIsPlaying(true)
    }
  }, [isOpen, demoSteps])

  useEffect(() => {
    if (currentStep < demoSteps.length) {
      setIsGenerating(true)
      const targetCode = demoSteps[currentStep].code

      // Simulate typing effect
      let i = 0
      const typeCode = () => {
        if (i <= targetCode.length) {
          setCode(targetCode.substring(0, i))
          i++
          setTimeout(typeCode, 20)
        } else {
          setIsGenerating(false)
        }
      }

      setCode('')
      typeCode()
    }
  }, [currentStep, demoSteps])

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying)
  }

  const handleReset = () => {
    setCurrentStep(0)
    setIsPlaying(true)
  }

  if (!isOpen) return null

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
