      }
      if (rendererRef.current) {
        rendererRef.current.dispose()
      }
    }
  }, [mountRef])

  return (
    <div 
      ref={mountRef} 
      className={`fixed inset-0 pointer-events-none ${className}`}
      style={{ zIndex: 1 }}
    />
  )
